package org.example.lab4.bean;import org.springframework.stereotype.Component;@Componentpublic class SchoolInformation {    private String fullname = "FPT Polytechnic - Bean";    private String location = "TPHCM";    public SchoolInformation(){}    public String getFullname() {        return fullname;    }    public void setFullname(String fullname) {        this.fullname = fullname;    }    public String getLocation() {        return location;    }    public void setLocation(String location) {        this.location = location;    }}