package org.example.lab4.bean_config;import org.example.lab4.bean.SchoolInformation;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class BeanSchoolInformationConfig {    @Bean("schoolInformationConfig")    public SchoolInformation schoolInformationConfig() {        SchoolInformation schoolInformation = new SchoolInformation();        schoolInformation.setFullname("FPT Polytechnic - Config");        schoolInformation.setLocation("Thành phố Hồ Chí Minh");        return schoolInformation;    }}