package org.example.java5jpa.controller;import org.example.java5jpa.entity.Role;import org.example.java5jpa.repository.RoleRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.HashMap;import java.util.Map;@RestController@RequestMapping("/role-api")public class RoleApi {    @Autowired    public RoleRepository roleRepository;    @GetMapping("/getAllRole")    public ResponseEntity<?> doGetAllRole() {        Map<String, Object> result = new HashMap<>();        result.put("success", true);        result.put("status", "Call Api success");        result.put("data", roleRepository.findAll());        return ResponseEntity.ok(result);    }    @PostMapping("/postAllRole")    public ResponseEntity<?> doPostAllRole(@RequestBody Role role) {        Map<String, Object> result = new HashMap<>();        result.put("success", true);        result.put("status", "Call Api success");        result.put("data", roleRepository.saveAndFlush(role));        return ResponseEntity.ok(result);    }}