package org.example.java5jpa.entity;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "Users")public class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "user_id")    private int userId;    @Column(name = "user_name")    private String username;    @Column(name = "password")    private String password;    @Column(name = "address")    private String address;    @Column(name = "full_name")    private String fullName;    @Column(name = "age")    private int age;    @Column(name = "diem")    private int diem;    @Column(name = "hoc_luc")    private String hocLuc;    @OneToOne(fetch = FetchType.EAGER)    @JoinColumn(name = "role_id", referencedColumnName = "role_id")    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private Role role;}