package org.example.java5jpa.service.impl;import org.example.java5jpa.entity.User;import org.example.java5jpa.repository.UserRepository;import org.example.java5jpa.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class UserServiceImpl implements UserService {    @Autowired    public UserRepository userRepository;    @Override    public User doSaveUser(User user) {        if (user.getDiem() >= 8) {            user.setHocLuc("Gioi");        } else if (user.getDiem() > 6) {            user.setHocLuc("Kha");        } else {            user.setHocLuc("Yeu");        }        return userRepository.saveAndFlush(user);    }    @Override    public void doDeleteUserById(int userId) {        userRepository.deleteById(userId);    }    @Override    public List<User> findAllUser() {        return userRepository.findAll();    }    @Override    public User findByUsername(String username) {        return userRepository.findByUsername(username);    }}